var media_item_id=2;
function new_media_item(){
    var _container = $('.am-media-preview');
    if(media_item_id > 8){
        showAlert(403,'最多只能添加8个图文消息。您超过了噢');
        return false;
    }
    _container.append('<div class="media-item" id="'+media_item_id+'"></div>');
    media_item_id++;
}